"use client";

import Link from "next/link";
import type { User } from "next-auth";
import { signOut } from "next-auth/react";

import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

import { UserAvatar } from "@/components/layout/docs/user-avatar";

interface UserAccountNavProps extends React.HTMLAttributes<HTMLDivElement> {
    user: Pick<User, "name" | "image" | "email">;
    params: {
        lang: string;
    };
    dict: Record<string, string>;
}

export function UserAccountNav({
    user,
    params: { lang },
    dict,
}: UserAccountNavProps) {
    return (
        <DropdownMenu>
            <DropdownMenuTrigger>
                <UserAvatar
                    user={{
                        name: user.name ?? null,
                        image: user.image ?? null,
                    }}
                    className="h-8 w-8"
                />
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
                <div className="flex items-center justify-start gap-2 p-2">
                    <div className="flex flex-col space-y-1 leading-none">
                        {user.name && (
                            <p className="font-medium">{user.name}</p>
                        )}
                        {user.email && (
                            <p className="w-[200px] truncate text-sm text-muted-foreground">
                                {user.email}
                            </p>
                        )}
                    </div>
                </div>
                <DropdownMenuSeparator />
                <DropdownMenuItem asChild>
                    <Link href={`/${lang}/dashboard`}>{dict.dashboard}</Link>
                </DropdownMenuItem>
                <DropdownMenuItem asChild>
                    <Link href={`/${lang}/dashboard/settings`}>
                        {dict.settings}
                    </Link>
                </DropdownMenuItem>
                <DropdownMenuSeparator />
                <DropdownMenuItem
                    className="cursor-pointer"
                    onSelect={(event) => {
                        event.preventDefault();
                        signOut();
                    }}
                >
                    {dict.sign_out}
                </DropdownMenuItem>
            </DropdownMenuContent>
        </DropdownMenu>
    );
}

import Link from "next/link"
import { CompletedForm, Form } from "@prisma/client"

import { formatDate } from "@/lib/utils"
import { Skeleton } from "@/components/ui/skeleton"
import { FormOperations } from "./form-operations"

interface FormItemProps {
    form: Pick<Form, "id" | "createdAt" | "title">;
}

export function FormItem({ form }: FormItemProps) {
  return (
    <div className="flex items-center justify-between p-4">
      <div className="grid gap-1">
        <Link
          href={`/editor/${form.id}`}
          className="font-semibold hover:underline"
        >
          {form.title}
        </Link>
        <div>
          <p className="text-sm text-muted-foreground">
            {formatDate(form.createdAt?.toDateString())}
          </p>
        </div>
      </div>
      <FormOperations form={{ id: form.id, title: form.title }} />
    </div>
  )
}

FormItem.Skeleton = function PostItemSkeleton() {
  return (
    <div className="p-4">
      <div className="space-y-3">
        <Skeleton className="h-5 w-2/5" />
        <Skeleton className="h-4 w-4/5" />
      </div>
    </div>
  )
}

