"use client";

import React from "react";
import Image from "next/image";
import Link from "next/link";
import { useSelectedLayoutSegment } from "next/navigation";

import { cn } from "@/lib/utils";
import {Icons} from "@/components/ui/icons";

import { MobileNav } from "../mobile-nav";
import type { MainNavItem } from "@/types";
import { siteConfig } from "@/config/site";

interface MainNavProps {
  items?: MainNavItem[];
  children?: React.ReactNode;
  params: {
    lang: string;
  };
}

export function MainNav({ items, children, params: { lang } }: MainNavProps) {
    const segment = useSelectedLayoutSegment();
    const [showMobileMenu, setShowMobileMenu] = React.useState<boolean>(false);
    const toggleMenu = () => {
        setShowMobileMenu(!showMobileMenu);
    };
    const handleMenuItemClick = () => {
        toggleMenu();
    };
    return (
        <div className="flex gap-6 md:gap-10">
            <Link
                href={`/${lang}`}
                className="hidden items-center space-x-2 md:flex"
            >
                <div>
                    <Icons.tnsLogo className="h-6 w-6" />
                </div>
                <div className="text-2xl font-semibold">{siteConfig.name}</div>
            </Link>
            {items?.length ? (
                <nav className="hidden gap-6 md:flex">
                    {items?.map((item, index) => (
                        <Link
                            key={index}
                            href={item.disabled ? "#" : `/${lang}${item.href}`}
                            className={cn(
                                "flex items-center text-lg font-medium transition-colors hover:text-foreground/80 sm:text-sm",
                                item.href?.startsWith(`/${segment}`)
                                    ? "text-foreground"
                                    : "text-foreground/60",
                                item.disabled && "cursor-not-allowed opacity-80"
                            )}
                        >
                            {item.title}
                        </Link>
                    ))}
                </nav>
            ) : null}
            <button
                className="flex items-center space-x-2 md:hidden"
                onClick={() => setShowMobileMenu(!showMobileMenu)}
            >
                {showMobileMenu ? <Icons.close /> : <Icons.logo />}
                <span className="font-bold">Menu</span>
            </button>
            {showMobileMenu && items && (
                <MobileNav items={items} menuItemClick={handleMenuItemClick}>
                    {children}
                </MobileNav>
            )}
        </div>
    );
}

